import React from 'react';
import Navbar from './components/layout/Navbar';
import Users from './components/users/Users';
import Search from './components/users/Search';

import axios from 'axios';
import './App.css';

class App extends React.Component {

  state = {
    users: [],
    loading: false
  }

  // async componentDidMount () {
  //   // changing states
  //   this.setState({loading: true});
  //   const res = await axios.get(`https://api.github.com/users?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secrets=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`);
  //   // res is request
  //   this.setState({ users: res.data, loading: false });
  // }

  // search github users
  searchUsers = async text => {
    this.setState({ loading: true }); // spinner as it searches

    const res = await axios.get(`https://api.github.com/search/users?q=${text}&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secrets=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`);
    // res is request
    this.setState({ users: res.data.items, loading: false });
  }

  // clear users from state
  clearUsers = () => this.setState({ users: [], loading: false });

  render(){
    return (
      <div className = 'App'>
        <Navbar />
        <div className="container">
          <Search searchUsers={this.searchUsers} clearUsers={this.clearUsers} showClear ={
            this.state.users.length > 0 ? true: false }
          />
          <Users loading={this.state.loading} users={this.state.users}/></div>
      </div>
    );
  }
}

export default App;
