import React, { Fragment, useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Navbar from './components/layout/Navbar';
import Users from './components/users/Users';
import User from './components/users/User';
import Search from './components/users/Search';
import Alert from './components/layout/Alert'; 
import About from './components/pages/About'

import axios from 'axios';

import GithubState from './context/github/GithubState';

import './App.css';

const App = (props) => {
  const [user, setUser] = useState({}); // {} means object
  const [repos, setRepos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState(null);

  // state = {
  //   users: [],
  //   user: {},
  //   loading: false,
  //   alert: null,
  //   repos: []
  // }

  // async componentDidMount () {
  //   // changing states
  //   this.setState({loading: true});
  //   const res = await axios.get(`https://api.github.com/users?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secrets=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`);
  //   // res is request
  //   this.setState({ users: res.data, loading: false });
  // }

  // get users repos
  const getUserRepos = async (username) => {
    setLoading(true);

    const res = await axios.get(
      `https://api.github.com/users/${username}/repos?per_page=5&sort=created:asc&client_id=${
        process.env.REACT_APP_GITHUB_CLIENT_ID}
        &client_secrets=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`);
    // res is request
    //this.setState({ repos: res.data, loading: false });
    setRepos(res.data);
    setLoading(false);
  }

  // set alert
  const showAlert = (msg, type) => {
    setAlert({ msg, type });

    setTimeout(() => setAlert(null), 5000)
  }

  return (
    <GithubState>
      <Router>
        <div className = 'App'>
          <Navbar />
          <div className="container">
            <Alert alert={this.state.alert} />
            <Switch>
              <Route exact
                path='/'
                render={props => (
                <Fragment>
                  <Search
                  setAlert={showAlert}
                  />
                  <Users />
                </Fragment>
              )} />
              <Route exact path='/about' component={About} />
              <Route exact path='/user/:login' render={props =>(
                <User {...props }
                getUserRepos={getUserRepos}
                repos={repos}
                user={user} loading={loading}
                alert={alert} />
              )} />
            </Switch>
          </div>
        </div>
      </Router>
    </GithubState>
  )
}

export default App;
