import React, { Fragment, useEffect } from 'react'
import Spinner from '../layout/Spinner'
import Repos from '../repos/Repos'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'

// replacement of component did mount class to a functional component
// is using useEffect hook

// props and methods
const User = ({ user, loading, getUser, getUserRepos, repos, match }) => {
    useEffect( () => {
        getUser(match.params.login);
        getUserRepos(match.params.login);
        // eslint-disable-next-line
    }, []);

    // in [] goes dependacies, requests for stuff only once
    // the eslint thing disables the warning that asks to put
    // getUser and getUserRepos as dependacies in []

    // componentDidMount() {
    //     this.props.getUser(match.params.login);
    //     this.props.getUserRepos(tmatch.params.login);

    //     // log in was taken from App.js where route path :login
    //

    const {
        name,
        company,
        avatar_url,
        location,
        bio,
        blog,
        login,
        html_url,
        followers,
        following,
        public_repos,
        public_gists,
        hireable
    } = user;

    // from github api

    if (loading) return <Spinner />;

    // the rendering when it actually fetches information from api

    return (
        <Fragment>
            <Link to='/' className='btn btn-light'>
                Back to Search</Link>
            Hireable: { ' ' }
            {hireable ? <i className='fas fa-check text-success' /> :
            <i className='fas fa-times-circle text-danger' />}
            <div className="card grid-2">
                <div className="all-center">
                    <img src={avatar_url} 
                    className="round-img" alt="" 
                    style={{width: '150px'}} />
                    <h1>{name}</h1>
                    <p>Location: {location}</p>
                </div>
                <div>
                    {bio && (<Fragment>
                        <h3>Bio</h3>
                        <p>{bio}</p>
                        </Fragment>)}
                    <a href={html_url} className="btn btn-dark my-1">
                        Visit GitHub Profile
                    </a>
                    <ul>
                        <li>
                            {login && <Fragment>
                                <strong>
                                    Username: </strong> {login}
                                    </Fragment>}
                        </li>
                        <li>
                            {company && <Fragment>
                                <strong>
                                    Company: </strong> {company}
                                    </Fragment>}
                        </li>
                        <li>
                            {blog && <Fragment>
                                <strong>
                                    Website: </strong> {blog}
                                    </Fragment>}
                        </li>
                    </ul>
                </div>
            </div>

            <div className="card text-center">
                <div className='badge badge-primary'>Followers: {followers}</div>
                <div className='badge badge-success'>Following: {following}</div>
                <div className='badge badge-light'>Public Repos: {public_repos}</div>
                <div className='badge badge-dark'>Public Gists: {public_gists}</div>



            </div>

            <Repos repos={repos} />
        </Fragment>
    )
}

User.propTypes = {
    loading: PropTypes.bool,
    user: PropTypes.object.isRequired,
    repos: PropTypes.array.isRequired,
    getUser: PropTypes.func.isRequired,
    getUserRepos: PropTypes.func.isRequired
}

export default User
