import React, { Component } from 'react'

export class User extends Component {
    componentDidMount() {
        this.props.getUser(this.props.match.params.login);
        // login was taken from App.js where route path :login
    }

    render() {

        const {
            name,
            avatar_url,
            location,
            bio,
            blog,
            login,
            html_url,
            followers,
            following,
            public_repos,
            public_gists,
            hireable
        } = this.props.user;

        // from github api

        const { loading } = this.props;

        // the rendering when it actually fetches information from api

        return (
            <div>
                {name}
            </div>
        )
    }
}

export default User
